import React, { useState } from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import "./styles.css";

function App() {
  const [colleges, setcolleges] = useState(null);

  const fetchData = async () => {
    const response = await axios.get(
      "http://localhost:5000/colleges"
    );

    setcolleges(response.data);
  };

  return (
    <div className="App">
      <h1>List of Colleges</h1>
      <h2>Fetch a list from an API and display it</h2>

      {/* Fetch data from API */}
      <div>
        <button className="fetch-button" onClick={fetchData}>
          Fetch Data
        </button>
        <br />
      </div>

      {/* Display data from API */}
      <div className="colleges">
        {colleges &&
          colleges.map((college, index) => {
            const cleanedDate = new Date(college.Timestamp).toDateString();
            // const authors = college.authors.join(", ");

            return (
              <div className="college" key={index}>
                <h3>college {index + 1}</h3>
                <h2>{college.name}</h2>

                <div className="details">
                  <p>Title: {college.Name}</p>
                  <p>Recognition: {college.Recognition}</p>
                  <p>Address: {college.Address}</p>
                  <p>‚è∞: {cleanedDate}</p>
                </div>
              </div>
            );
          })}
      </div>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
